import { FETCH_COMMENT, SAVE_COMMENT } from "actions/types";

export default function(state = [],action){
    switch (action.type){
        case SAVE_COMMENT:
            return [...state, action.payload];
        case FETCH_COMMENT:
            return [...state]
        default:
            return state;
    }
}
